import { Component, OnInit } from '@angular/core';
import { AuthService } from '../../services/auth.service';
import { StorageService } from '../../services/storage.service';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {
  form: any = {
    username: null,
    password: null
  }

  isLoggedIn = false;
  isLoginFailed = false;
  errorMessage = '';
  roles: string[] = [];

  constructor(private authService: AuthService, private storageService: StorageService) { }

  ngOnInit(): void {
    if (this.storageService.isLoggedIn()) {
      this.isLoggedIn = true;
      this.roles = this.storageService.getUser().roles;
    }
  }

  onSubmit(): void {
    const { username, password } = this.form;

    this.authService.login(username, password).subscribe({
      next: data => {
        console.log("Gets Here")
        this.storageService.saveUser(data);

        this.isLoginFailed = false;
        this.isLoggedIn = true;
        console.log(this.storageService.getUser())
        this.roles = this.storageService.getUser().roles;
        this.reloadPage();
      },
      error: err => {
        console.log(this)
        this.errorMessage = err.error.message;
        this.isLoginFailed = true;
      }
    });
  }

  reloadPage(): void {
    window.location.reload();
  }

}


<ul class="nav navbar-nav flex-row justify-content-between ml-auto">
  <li *ngIf="isLoggedIn">
    {{ storageService.getUser() }}
  </li>

  <li class="nav-item order-2 order-md-1"><a href="#" class="nav-link" title="settings"><i class="fa fa-cog fa-fw fa-lg"></i></a></li>
  <li class="dropdown order-1">
    <button type="button" id="dropdownMenu1" *ngIf="!isLoggedIn" data-toggle="dropdown" class="btn btn-outline-secondary dropdown-toggle">Login <span class="caret"></span></button>
    <ul  *ngIf="!isLoggedIn" class="dropdown-menu dropdown-menu-right mt-1">
      <li class="p-3">
        <form class="form" role="form" (ngSubmit)="f.form.valid && onSubmit()" #f="ngForm">
          <div class="form-group">
            <input id="username" name="username" placeholder="Username" class="form-control form-control-sm" type="text" [(ngModel)]="form.username" required #username="ngModel" [ngClass]="{ 'is-invalid': f.submitted && username.errors }">
          </div>
          <div class="form-group">
            <input id="password" placeholder="Password" class="form-control form-control-sm" name="password" type="password" required [(ngModel)]="form.password" #password="ngModel" [ngClass]="{ 'is-invalid': f.submitted && password.errors }">
          </div>
          <div class="form-group">
            <button type="submit" class="btn btn-primary btn-block">Login</button>
          </div>
        </form>
      </li>
    </ul>
  </li>
</ul>
